      }


      render() {
        return (
<Map region={this.state.region}
onPress={(e) => this.setState({ marker: e.nativeEvent.coordinate })}>
{
      this.state.marker &&
      <Map.Marker coordinate={this.state.marker} />
}
</Map>
        );
      }
    }
    export default Map;
